import React, { Component } from "react";
import axios from "axios";

const Message = props => (
  <tr>
    <td>
      <p>{props.message}</p>
    </td>
  </tr>
);

export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:5000/api/messages").then(res => {
      console.log(res.data.messages);
      if (res.data.length > 0) {
        this.setState({
          messages: res.data.messages
        }).catch(err => {
          console.log(err);
        });
      }
    });
  }

  messageList() {
    return this.state.messages.map(currentMessage => {
      console.log(currentMessage);
      return <Message message={currentMessage} />;
    });
  }
  render() {
    return (
      <div className=" card col l6 offset-l8 s12">
        <h1 className="card-title">LATEST REQUESTS</h1>
        <div className="card-content">
          <table className="striped">
            <tbody>{this.messageList()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}
