import React, { Component } from "react";
import axios from "axios";

const Message = props => (
  <tr>
    <td className="row">
      <p className="col l6">{props.message.description}</p>

      <p className=" col offset-l6">
        {props.message.date
          .substring(0, 10)
          .split("-")
          .reverse()
          .join("/")}
      </p>

      <p>{props.message.name}</p>
    </td>
  </tr>
);

export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/api/messages")
      .then(res => this.setState({ messages: res.data.messages }));
  }

  render() {
    return (
      <div className=" card col l6 offset-l8 s12">
        <h1 className="card-title">LATEST REQUESTS</h1>
        <div className="card-content">
          <table className="striped">
            <tbody>
              {this.state.messages.map(message => (
                <Message key={message._id} message={message} />
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
