import React from "react";

class musicplayer extends React {
  constructor(props) {
    super(props);
    this.state = {
      playing: false
    };
  }
}

handlePlayerClick = () => {
  if (!this.state.playing) {
    this.setState({ playing: true });
  } else {
    this.setState({ playing: false });
  }
  render();
  return (
    <p onClick={this.state.playing ? this.pauseSong : this.playSong}>
      Play/Pause
    </p>
  );
};

export default musicplayer;
