export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:5000/api/messages").then(res => {
      if (res.data.length > 0) {
        this.setState({
          messages: res.data.messages.map(message => message.name)
        }).catch(err => {
          console.log(err);
        });
      }
    });
  }

