import React, { Component } from "react";
import axios from "axios";
import M from "materialize-css";

const Message = props => (
  <tr>
    <td className="row">
      <p id="message" className="col s7">
        {props.message.description}
      </p>

      <p id="date" className="col s4">
        {props.message.date
          .substring(0, 10)
          .split("-")
          .reverse()
          .join("/")}
      </p>
      <div className="row">
        <p id="name">{props.message.name}</p>
      </div>
    </td>
  </tr>
);

export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/api/messages")
      .then(res => this.setState({ messages: res.data.messages }));
  }
  // sortList = () => {
  //   const { messages } = this.state;

  // };

  render() {
    return (
      <div className=" card col l6 offset-l8 s12 ">
        <h1 className="card-title">LATEST REQUESTS</h1>
        <div className="card-content divWithScroll">
          <table className="striped  ">
            <tbody>
              {this.state.messages.map(message => (
                <Message key={message._id} message={message} />
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
