import React, { Component } from "react";
import axios from "axios";

const Message = props => (
  <tr>
    <td>
      {props.message.name}
      {props.message.description}
      {props.message.date.substring(0, 10)}
    </td>
  </tr>
);

export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:5000/api/messages").then(res => {
      this.setState({ messages: res.data }).catch(err => {
        console.log(err);
      });
    });
  }

  requestsGroup() {
    return this.state.messages.map(currentMessage => {
      return( 
      <Message message={currentMessage} />
      );
    });
  }
  render() {
    return (
      <div className=" card col l6 offset-l8 s12">
        <h1 className="card-title">LATEST REQUESTS</h1>
        <div className="card-content">
          <table className="striped">
            <tbody>{this.requestsGroup()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}
