import React, { Component } from "react";
import axios from "axios";

const Message = props => (
  <tr>
    <td>
      <div className="row">
        <div className="col l6">
          <p>{props.message.description}</p>
        </div>
        <div className="col l6">
          <p>{props.message.date.dateFormat(props.message.date)}</p>
        </div>
      </div>
    </td>
  </tr>
);

export default class requestlist extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: []
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/api/messages")
      .then(res => this.setState({ messages: res.data.messages }));
  }
  dateFormat(date) {
    let formatDate = date.getMonth() + date.getDate() + date.getYear();
    return formatDate;
  }
  render() {
    return (
      <div className=" card col l6 offset-l8 s12">
        <h1 className="card-title">LATEST REQUESTS</h1>
        <div className="card-content">
          <table className="striped">
            <tbody>
              {this.state.messages.map(message => (
                <Message key={message._id} message={message} />
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
